package com.sjz.zyl.appdemo.utils;/** * Description * * @author by 张迎乐(yingle1991@gmail.com) * @since 2018-02-17. */import android.graphics.Color;import android.text.TextPaint;import android.text.style.UnderlineSpan;/** * 无下划线的Span * Author: msdx (645079761@qq.com) * Time: 14-9-4 上午10:43 */public class NoUnderlineSpan extends UnderlineSpan {    @Override    public void updateDrawState(TextPaint ds) {        ds.setColor(Color.GRAY);        ds.setUnderlineText(false);    }}